import { Router } from "express";
import bikePointsListsHandler from "./bikePointsLists.handler.js";
import { authenticate } from "../../middleware/middlewares.js";
const bikePointsListsRoutes = Router();
bikePointsListsRoutes.get("/", authenticate, bikePointsListsHandler.getAllBikePointsLists);
bikePointsListsRoutes.post("/", authenticate, bikePointsListsHandler.createBikePointsList);
bikePointsListsRoutes.put("/name", authenticate, bikePointsListsHandler.updateBikePointsListName);
bikePointsListsRoutes.put("/add", authenticate, bikePointsListsHandler.updateBikePointsListAdd);
bikePointsListsRoutes.put("/", authenticate, bikePointsListsHandler.updateBikePointsList);
bikePointsListsRoutes.delete("/", authenticate, bikePointsListsHandler.deleteBikePointsList);
bikePointsListsRoutes.delete("/name", authenticate, bikePointsListsHandler.deleteBikePoint);
export default bikePointsListsRoutes;
